REPLACE INTO `purpose` VALUES ('guide-dashboard-user-personal','Sólo debes cumplimentar estos datos si has creado un proyecto y quieres que sea cofinanciado y apoyado.\r\n\r\nLa información de este apartado es necesaria para contactarte en caso de que obtengas la financiación requerida, y que así se pueda efectuar el ingreso.',NULL,'general');
