REPLACE INTO `purpose` VALUES ('guide-dashboard-user-profile','Tanto si quieres presentar un proyecto como incorporarte como cofinanciador/a, para formar parte de la comunidad te recomendamos que pongas atención en tu texto de presentación, que añadas links relevantes sobre lo que haces y subas una imagen de perfil con la que te identifiques.',NULL,'dashboard');
