REPLACE INTO `purpose` VALUES ('tooltip-project-cost-type','Aquí debes especificar el tipo de coste: vinculado a una tarea (algo que requiere la habilidad o conocimientos de alguien), la obtención de material (consumibles, materias primas) o bien infraestructura (espacios, equipos, mobiliario).',NULL,'costs');
